mapper.select:
select rownum, tb.* from(
	select rNum, memo, endWrite from resume where mId=#{mId} order by rNum desc 
)tb

select rNum from(
	select rownum, tb.* from(
		select rNum from resume where mId=#{mId}
		order by rNum desc
	)tb where rownum=1
)

mapper.update:
update resume set rNum=#{rNum}, rName=#{rName}, resumeType=#{resumeType}, rPhoto=#{rPhoto}, engName=#{engName}, apply=#{apply}, 
pay=#{pay}, phone=#{phone}, eMail=#{eMail}, addr=#{addr}, addr2=#{addr2}, army=#{army}
where rNum=#{rNum}
