mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT claimNum, b.mId, subject
			           ,TO_CHAR(b.created, 'YYYY-MM-DD') created
					   ,hitCount, category, m.mName
			        FROM claim b JOIN member m ON b.mId=m.mId
                    
                         
			                  
		                 
	                
	                ORDER BY claimNum DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
