mapper.select:
select mId, rNum, rName, subTypes from( select rownum, tb.* from( select mId, r.rNum, rName, LISTAGG(s.subName, ',') WITHIN GROUP(ORDER BY s.subCode) subTypes from member m join resume r on m.mId=r.mId left join intro i on r.rNum=i.rNum join interest_job ij on m.mId=ij.mId join sub_class s on ij.subCode=s.subCode join main_class mc on s.maCode=mc.maCode where resumeType=1
and ()
group by mId, r.rNum, rName )tb where rownum<=#{end} )where rownum>=#{start}

select count(*) from( select r.mId, r.rNum, rName, LISTAGG(s.subName, ',') WITHIN GROUP(ORDER BY s.subCode) subTypes from storagy s left join resume r on s.rNum=r.rNum join interest_job ij on r.mId=ij.mId join sub_class s on ij.subCode=s.subCode join main_class mc on s.maCode=mc.maCode where s.mId=#{mId}
and
group by r.mId, r.rNum, rName )

select mId, rNum, rName from( 
	select rownum, tb.* from( 
		select r.mId, r.rNum, rName, LISTAGG(s.subName, ',') WITHIN GROUP(ORDER BY s.subCode) subTypes 
		from storagy s 
		left join resume r on s.rNum=r.rNum 
		join interest_job ij on r.mId=ij.mId 
		join sub_class s on ij.subCode=s.subCode 
		join main_class mc on s.maCode=mc.maCode 
		where s.mId=#{mId}
	
		and 
	
		group by r.mId, r.rNum, rName
	)tb where rownum<=#{end} 
)where rownum>=#{start}
